package cn.caecc.erp.modules.dao.ex.dto;

import java.util.List;
import java.util.Map;

import cn.caecc.erp.modules.dao.mybatis.entity.Menu;

/**
 * @author weizhen
 *
 */
public class MenuDto extends Menu {

	/**
	 * 
	 */
	private static final long serialVersionUID = 1L;

	/**
	 * 
	 */
	private String pMenuName;
	
	private String pAction;
	
	private String pIcon;
	
	private List<MenuDto> subMenuList;
	
	private Map<String, MenuDto> subMenuMap;
	
	public MenuDto() {
		// TODO Auto-generated constructor stub
	}

	/**
	 * @return the pMenuName
	 */
	public String getpMenuName() {
		return pMenuName;
	}

	/**
	 * @param pMenuName the pMenuName to set
	 */
	public void setpMenuName(String pMenuName) {
		this.pMenuName = pMenuName;
	}

	
	public String getpIcon() {
		return pIcon;
	}

	public void setpIcon(String pIcon) {
		this.pIcon = pIcon;
	}

	/**
	 * @return the pAction
	 */
	public String getpAction() {
		return pAction;
	}

	/**
	 * @param pAction the pAction to set
	 */
	public void setpAction(String pAction) {
		this.pAction = pAction;
	}

	/**
	 * @return the subMenuList
	 */
	public List<MenuDto> getSubMenuList() {
		return subMenuList;
	}
	
	/**
	 * @param subMenuList the subMenuList to set
	 */
	public void setSubMenuList(List<MenuDto> subMenuList) {
		this.subMenuList = subMenuList;
	}

	/**
	 * @return the subMenuMap
	 */
	public Map<String, MenuDto> getSubMenuMap() {
		return subMenuMap;
	}

	/**
	 * @param subMenuMap the subMenuMap to set
	 */
	public void setSubMenuMap(Map<String, MenuDto> subMenuMap) {
		this.subMenuMap = subMenuMap;
	}

}
